// construct the GET request for our DfE Information endpoint
var client = new RestSharp.RestClient('https://dev-api-customerengagement.platform.education.gov.uk');
var request = new RestSharp.RestRequest('dfe/open-info', RestSharp.Method.GET);
request.AddHeader('Ocp-Apim-Subscription-Key', '992519688c7c43ef916add082ac49e33');

// Add Subscription Key Header
request.AddHeader("Ocp-Apim-Subscription-Key", "992519688c7c43ef916add082ac49e33");

// Execute the query and retreive results
var queryResult = client.Execute(request);

// extract the HTTP status
HttpStatusCode statusCode = queryResult.StatusCode; int numericStatusCode = (int)statusCode;

// construct the GET request for access token
var client = new RestClient('<Paste OAuth Token URL Here>');
var request = new RestRequest(Method.POST);
request.AddHeader("content-type", "application/x-www-form-urlencoded");
request.AddParameter("application/x-www-form-urlencoded
grant_type=client_credentials&client_id=<Paste client id here>&client_secret=<Paste client secret here>", ParameterType.RequestBody);

// make call to get token
IRestResponse response = client.Execute(request);

// extract token
dynamic resp = JObject.Parse(response.Content);
string token = resp.access_token;

// prepare and make an api call to application authenticated endpoint
var client = new RestClient("https://oat-api-customerengagement.platform.education.gov.uk");
var request = new RestRequest("dfe/application-info", Method.GET);
request.AddHeader("authorisation", "Bearer " + token);
request.AddHeader("ocp-apim-subscription-key", "992519688c7c43ef916add082ac49e33");
request.AddHeader("accept", "application/json; charset=utf-8");
response = client.Execute(request);

// make a call to token endpoint to get access token
var client = new RestClient('<Paste OAuth Token URL Here>');
var request = new RestRequest(Method.POST);
request.AddHeader("content-type", "application/x-www-form-urlencoded");
request.AddParameter("application/x-www-form-urlencoded
grant_type=client_credentials&client_id=<Paste your client id here>&client_secret=<Paste your client secret here>&code=<Paste your authorisation code here>&redirect_URL=<Paste your redirect url here>", ParameterType.RequestBody);
IRestResponse response = client.Execute(request);

// Extract token
dynamic resp = JObject.Parse(response.Content);
string token = resp.access_token;

// prepare and make an API call to application authenticated endpoint
var client = new RestClient("https://oat-api-customerengagement.platform.education.gov.uk");
var request = new RestRequest("dfe/user-info", Method.GET);
request.AddHeader("authorisation", "Bearer " + token);
request.AddHeader("ocp-apim-subscription-key", "992519688c7c43ef916add082ac49e33");
request.AddHeader("accept", "application/json; charset=utf-8");
response = client.Execute(request);

// construct the GET request for refresh token
var client = new RestClient("<Paste token endpoint here>");
var request = new RestRequest(Method.POST);
request.AddHeader("content-type", "application/x-www-form-urlencoded");
request.AddParameter("application/x-www-form-urlencoded
grant_type=client_credentials&client_id=<Paste your client id here>&client_secret=<Paste your client secret here>&code=<Paste your authorisation code here>", ParameterType.RequestBody);

// make a call to get refresh token
IRestResponse response = client.Execute(request);
